UPDATE lessons SET orderIndex = orderIndex + 5 WHERE moduleId = (SELECT id FROM modules WHERE slug = 'norte');

-- Depois, mover lições do Marco Zero para o NORTE
UPDATE lessons SET moduleId = (SELECT id FROM modules WHERE slug = 'norte') WHERE moduleId = (SELECT id FROM modules WHERE slug = 'marco-zero');

-- Ajustar orderIndex das lições do Marco Zero para ficarem no início (1-5)
