UPDATE lessons SET orderIndex = 4 WHERE moduleId = (SELECT id FROM modules WHERE slug = 'norte') AND title = 'Seu perfil no Instagram';
UPDATE lessons SET orderIndex = 5 WHERE moduleId = (SELECT id FROM modules WHERE slug = 'norte') AND title = 'Checklist Final do Marco Zero';

